	'merchantID' => 'totsy',
	'transactionKey' => '',
	'endpoint' => 'test',
	'filters' => array(
		'alwaysProcessAdapter' => true,
		'adapter' => $adapterFilters['cybersource']
	)
);

$authorizenetTest = array(
